// Check if object is in array
function is_obj_in_array(obj, arr) {
    var is_present = false;

    for (var i = 0; i < arr.length; i++) {
        var o = arr[i];
        var all_keys_present = true;
        for (var key in obj) {
            if (!(key in o && o[key] === obj[key])) {
                all_keys_present = false;
                break;
            }
        }

        if (all_keys_present) {
            is_present = true;
            break;
        }
    }
    return is_present;
}

module.exports = {
    is_obj_in_array: is_obj_in_array
};